//Gestion des touches cliqu√©es dans les menus des PCB par l'utilisateur et cr√©ation des messages de retour ainsi que des topics MQTT
for (var myPCB in myDictKeyboard){                                                                                  // On parcour tous les PCB
    for (let myMsg = 0; myMsg < myDictKeyboard[myPCB].length ; myMsg+=2){                                           // On parcour tous les textes des boutons (indices pairs)

        if (recv.toUpperCase() == ("GO TO PCB " + myPCB.toUpperCase() )){                                           // Si j'ai cliqu√© sur un bouton PCB dans le menu (ex: Go To PCB MultiProtocol)
            msg.payload.content = "üí¨ I am in the *PCB "+ myPCB +"* ! Choose an option :";                          // Le bot me r√©pondra avec cette phrase
            global.set("PCBselected", myPCB.toUpperCase());                                                         // On conserve quel bouton PCB on avait cliqu√© (Mono/MultiProtocol...)
            keyboardList = [];                                                                                      // Cr√©ation d'une liste contenant les boutons accessible dans le menu du PCB
            pastpositionOfButtonInOneLine = 0;                                                                      // Pr√©paration de la variable d√©tectant si sur une ligne il y aura 1 ou plusieurs boutons
            columnOfLine = 0;                                                                                       // Pr√©paration de la variable positionnant le texte du deuxi√®me bouton ou plus sur une meme ligne 
            for (let indiceOfButton = 0; indiceOfButton< (myDictKeyboard[myPCB].length)/2 ; indiceOfButton++){      // On parcour les indices/positions des boutons du dictionnaire

                positionOfButtonInOneLine = myDictKeyboard[myPCB][(indiceOfButton*2)+1];                            // On r√©cup√®re les positions de chaque boutons (ex: 1, 2, 3, 3) dans chaque ligne

                if ( positionOfButtonInOneLine > pastpositionOfButtonInOneLine){                                    // Si sur la ligne je ne veux mettre qu'un bouton
                    keyboardList[indiceOfButton] = [myDictKeyboard[myPCB][indiceOfButton*2]];                       // On r√©cup√®re les valeurs des cl√©fs (texte des btn) pour les formater sous forme de liste keyboard, ex : [[a],[b],[c],...]
                    columnOfLine = 0;                                                                               // On r√©initialise la variable parce qu'il n'y a pour l'instant pas un deuxi√®me bouton sur la meme ligne
                }

                else if (positionOfButtonInOneLine == pastpositionOfButtonInOneLine){                               // Si sur la ligne je veux mettre 2 ou plusieurs boutons
                    columnOfLine++;                                                                                 // Sur la meme ligne il y a un deuxi√®me bouton (ou plus), donc on incr√©mente la variable
                    keyboardList[indiceOfButton-columnOfLine][columnOfLine] = myDictKeyboard[myPCB][(indiceOfButton*2)]; // On r√©cup√®re les valeurs des cl√©fs (texte des btn) pour les formater sous forme de liste keyboard, ex : [[a, b],[c, d, e],[f, g],...]
                }
                pastpositionOfButtonInOneLine = positionOfButtonInOneLine;                                          // On r√©cup√®re la pr√©c√©dente valeur de la position du bouton (1,2,3,3)
            }
            keyboardList = keyboardList.filter(n => n);                                                             // Il y a un emplacement vide qui apparait dans la liste parfois, donc on le filtre (un d√©faut de pointeur)
            keyboardList[keyboardList.length]= ["Back to Menu"];                                                    // On rajout un dernier bouton tout en bas, celui de revenir vers le menu

            opts = {                            // D√©but de cr√©ation des options du custom keyboard
              reply_markup: JSON.stringify({    // Pr√©paration du JSON
                keyboard: keyboardList,         // On reprend la liste formatt√©e pour l'ins√©rer dans le JSON (elle va permettre de positionner les boutons comme pr√©c√©dement format√© sous la variable keyboardList)
                  'resize_keyboard' : true,     // Param√®tre de mise en page
                  'one_time_keyboard' : true    // Param√®tre de mise en page
              }),
              disable_web_page_preview : true,  // Param√®tre de mise en page
              parse_mode : "Markdown"           // Param√®tre de mise en page
            };
            msg.payload.options = opts;         // Introduction des options JSON dans la charge utile
        }

        if (recv.toUpperCase() == (myDictKeyboard[myPCB][myMsg]).toUpperCase() && (global.get("PCBselected") == myPCB.toUpperCase())){              // Si j'ai cliqu√© dans une option d'un menu d'un PCB (ex : "Set LED ON" du PCB MultiProtocol)
            msg.payload.content = "üí¨ I am in the *PCB " + myPCB + "* and I will try to *" + myDictKeyboard[myPCB][myMsg] + "* ! Wait a moment..."; // Alors le bot me r√©pond
            global.set("MQTTrequest", (myPCB.toUpperCase()) + " " + (myDictKeyboard[myPCB][myMsg]).toUpperCase());                                  // Cr√©ation d'une chaine permettant de cr√©er la requete MQTT
            global.set("MQTTtopic", myPCB);                                                                                            // Cr√©ation du Topics MQTT qui contiendra la requete
        }
    }
}

return msg;

